INSERT INTO `products` VALUES ('115', 'pintura-genero-mate-37-ml', 'PINTURA GENERO MATE 37 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>Presentada en 123 colores mates (incluyendo Incoloro 500).</li>\n<li>Muy resistente a lavados.</li>\n<li>Excelente capa.</li>\n<li>Fijación al frío.</li>\n<li>Colores mezclables entre sí.</li>\n<li>Lista para el uso.</li>\n<li>Mayor rendimiento.</li>\n</ul>\n<p><strong>Diluyente:</strong><br></p>\n<ul>\n<li>Para diluir use Diluyente de Pintura para Tela. Indicado para dilución, retraso del secado, mezcla de colores y sombreado.</li>\n<li>Mantiene el poder de fijación y resistencia al lavado.</li>\n</ul>\n<h5>Modo de Us</h5>\n<ul>\n<li>Puede ser aplicada con pincel, esponja o sello de goma en telas de algodón sin goma (no sintéticos).</li>\n<li>Lavar la tela antes de la pintura para remover los residuos de goma.</li>\n<li>Lavar la tela solamente 72 horas después de la pintura.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Para telas de algodón.</li>\n<li>No utilice en telas sintéticas o engomadas.</li>\n</ul>', 10870144601, 100.00, 0, 1341.00, 1490.00, 7891153072357);
