INSERT INTO `products` VALUES ('127', 'craquelex-color-37-ml', 'CRAQUELEX COLOR 37 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>El set para craquelamento contiene,  barniz base n°1 (incoloro) y barniz craquelador n° 2 en el color especificado en la caja.</li>\n<li>Listo para su uso.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Para obtener el craquelado, aplique una capa de barniz base n.° 1 sobre la pieza previamente preparada, utilizando un pincel plano de pelos suaves, pincelando de manera a dejar una capa media.</li>\n<li>Aguarde el secado total. Después del secado, aplique una capa media del barniz craquelador n.° 2.</li>\n<li>Aguarde el secado para el efecto craquelado.</li>\n<li>Tips para obtener un craquelado mayor,cargue más el Barniz Base n.° 1.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Pintura con efecto craquelado. Puede ser aplicada sobre madera, yeso, cerámica y bastidor.</li>\n<li>En otras superficies recomendamos una prueba antes.</li>\n</ul>', 10870103801, 100.00, 22, 6471.00, 7190.00, 7891153057378);
INSERT INTO `products` VALUES ('128', 'pintura-craquelex-37-ml', 'PINTURA CRAQUELEX 37 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>Es formulada con resina acrílica.</li>\n<li>Presenta 19 colores, siendo 2 metálicos.</li>\n<li>Listo para uso.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Aplicar con pincel, esponja o espátula, sobre las superficies preparadas con Pintura Acrílica, Pintura Mate para Artesania o Metal Colors.</li>\n<li>El craquelado surgirá hasta 2 horas después de la aplicación y varía de acuerdo con la capa.</li>\n<li>Aplicar una capa de Base para Pintura Craquelex para un mejor craquelado.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Pintura con efecto craquelado.</li>\n<li>Puede ser aplicada sobre yeso, madera, cerámica y bastidor. No aplicar en superficies maleables.</li>\n</ul>', 10870100001, 100.00, 50, 2151.00, 2390.00, 7891153039619);
