INSERT INTO `products` VALUES ('155', 'marcador-de-tela-acrilpen', 'MARCADOR DE TELA ACRILPEN', '<p><strong>Características:</strong></p>\n<ul>\n<li>Excelente fijación.</li>\n<li>Resistente a lavados.</li>\n<li>Presentada en 12 colores.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Lavar la tela antes de la pintura para remover los residuos de goma.</li>\n<li>La punta del marcador Acrilpen permite hacer trazos de diferentes espesuras. Usando en la posición vertical, consigues trazos finos, en la posición inclinada, trazos más gruesos.</li>\n<li>Lavar solamente 72 horas después de la pintura.</li>\n<li>Después del uso, guardar en la posición horizontal.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Marcador para pintar y marcar tela permanentemente.</li>\n<li>Para telas de algodón.</li>\n<li>No utilice en telas sintéticas o engomadas.</li>\n</ul>', 10870310101, 100.00, 34, 2061.00, 2290.00, 7891153044316);
INSERT INTO `products` VALUES ('156', 'termolina-lechosa-100-ml', 'TERMOLINA LECHOSA 100 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>Lista para uso.</li>\n<li>Acabamiento incoloro después del secado.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Aplicar con pincel sobre la pintura ya seca. Para reforzar su efecto, aplicar dos capas por el revés y dos por la frente, intercalando el secado.</li>\n<li>No resistente a sucesivos lavados.</li>\n<li>Se recomienda una nueva aplicación siempre que la tela volver a ser moldeable.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Para proteger costuras y bordados, evitando el deshilachado en tabajos de recorte.</li>\n<li>Para telas de algodón.</li>\n<li>No utilizar en telas sintéticas o engomadas.</li>\n</ul>', 10870251001, 100.00, 475, 3141.00, 3490.00, 7891153165103);
