INSERT INTO `products` VALUES ('168', 'primer-para-metales-vidrios-y-plasticos-tipo-pet-100-ml', 'PRIMER PARA METALES, VIDRIOS Y PLÁSTICOS TIPO PET 100 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>Protege contra la corrosión en las superficies de aluminio, hierro y galvanizados.</li>\n<li>Soluble en agua.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Puede aplicar con un pincel o una esponja.</li>\n<li>En las piezas con barniz, lije antes de aplicar el Primer.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Utilizado como base para plásticos tipo PET y vidrio, posteriormente puede ser pintado con pinturas Acrílicas, Pintura Mate para Artesanía y Metal Colors.</li>\n<li>Se puede aplicar también en aluminio, metales, hierro y galvanizadas.</li>\n</ul>', 10870270001, 100.00, 95, 6291.00, 6990.00, 7891153048376);
