INSERT INTO `products` VALUES ('173', 'papel-liquido-250-ml', 'PAPEL LÍQUIDO 250 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>Efecto de papel.</li>\n<li>Super Versátil.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Antes de usar, mezclar ligeramente con pincel o espátula.</li>\n<li>Aplique una capa uniforme (evite capas delgadas) en un molde, por ejemplo: placa desechable, formas de acetato y toallas de plástico (superficies flexibles y no porosas).</li>\n<li>Espere 24 horas para que se seque antes de quitar el molde.</li>\n<li>Después del secado, se puede cortar con tijeras o stylus, pintar con pinturas acrílicas o P.V.A</li>\n<li>Puede pegar el papel terminado en otras superficies con pegamento Atelier o adhesivo instantáneo Acrilex.</li>\n</ul>', 10870252601, 100.00, 0, 8271.00, 9190.00, 7891153096674);
INSERT INTO `products` VALUES ('174', 'liquido-enmascarador-para-artesanato-35-m-l', 'LÍQUIDO ENMASCARADOR PARA ARTESANATO 35 M, L', '<p><strong>Características:</strong></p>\n<ul>\n<li>Base de látex natural, especial para uso en trabajos decorativos, aislando la área aplicada.</li>\n<li>Listo para uso.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Aplique con la punta.</li>\n<li>Después del secado, la película elástica de látex se vuelve resistente al agua.</li>\n<li>No deje que se seque durante un largo tiempo. Retire el filme después de pintar, frotando con el dedo o la goma de borrar.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Para aislar las áreas que no serán pintadas y también para la protección temporal (antes de pintar) en áreas metálicas como cierres y bisagras.</li>\n<li>Se puede aplicar en madera, papel, tela, yeso, vidrio, cuero y telgopor.</li>\n</ul>', 10870263101, 100.00, 149, 2151.00, 2390.00, 7891153075099);
