INSERT INTO `products` VALUES ('20', 'pintura-de-genero-con-expansion-acripuff-35-ml', 'PINTURA DE GENERO CON EXPANSIÓN ACRIPUFF 35 ML', '<p><strong>Características:</strong></p>\n<ul>\n<li>Presentada en 30 colores y la Base Incoloro.</li>\n<li>Listo para su uso.</li>\n</ul>\n<p><br></p>\n<h5>Modo de Uso</h5>\n<ul>\n<li>Puede aplicarse directamente con la punta del frasco o con pincel, en telas de algodón sin goma (no sintéticos).</li>\n<li>Lavar la tela antes de la pintura para remover los residuos de goma.</li>\n<li>Después del secado completo, de 24 horas, el Acripuff debe ser expandido con secadora para pelos en potencia máxima por la frente o planchado por el revés en la temperatura indicada para la tela.</li>\n<li>El Acripuff Base Incoloro debe ser mezclado con las Pinturas para Tela en la proporción de 3 partes de Base para 1 de Pintura para Tela y aplicada con pincel, de 3 a 4 capas.</li>\n</ul>\n<h5>Uso Específico</h5>\n<ul>\n<li>Pintura para expansión a calor.</li>\n<li>Presenta acabado en relieve encauchado.</li>\n<li>Para telas de algodón.</li>\n<li>No utilice en telas sintéticas o engomadas.</li>\n</ul>', 10870179201, 100.00, 3, 2061.00, 2290.00, 7891153032313);
